float fastness = 2.0;
float maxTiles =3.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float calc = (maxTiles * 2.0) / 3.0;
    float speed = (iTime * fastness);

    // messing with the 
    vec2 uv = fragCoord/vec2(iResolution.x /clamp(calc + cos(speed), 1.0, maxTiles), iResolution.y /clamp(calc + cos(speed), 1.0, maxTiles));

    // render the image
    vec3 col = texture(iChannel0,uv).rgb;

    // Output to screen
    fragColor = vec4(col,1.0);
}
