float fct(float x)
{
  return -sqrt(1.0-pow(x-1.0,2.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy-0.5;

    float shift = cos(iTime*5.0)*0.2;
    // Time varying pixel color
    float v1 = (fct(uv.x*2.0+shift)+1.0)*0.5+(fct(uv.y*2.0+shift)+1.0)*0.5;
    float v2 = (fct(-uv.x*0.3+shift)+1.0)*0.5+(fct(uv.y*0.5+shift)+1.0)*0.5;
    float v3 = (fct(uv.x*0.3+shift)+1.0)*0.5+(fct(-uv.y*0.5+shift)+1.0)*0.5;
    float v4 = (fct(-uv.x*2.0+shift)+1.0)*0.5+(fct(-uv.y*2.0+shift)+1.0)*0.5;
    float v = max(max(v1, v2), max(v3, v4));
    vec3 m = vec3(v);

    // Output to screen
    fragColor = vec4(m,1.0);

}
