#define SPEED 0.8

void mainImage( out vec4 O, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;    
    vec2 u = fragCoord/iResolution.y;    
    float t = iTime * SPEED;
    uv*=8.0;

    // uv.x+=t*0.1; // uncomment to move tiles in x axis
    vec2 c = vec2(0.5);
    vec2 fu = mod(uv,2.0)*0.5;
    uv = fract(uv)+step(fu.yx,vec2(.5))-0.5;

    // uv.x-=fu.x-0.5;
    //uv.y-=fu.y-0.5;
    float d = length(1.0*uv-.5);

    float smooth1 = 0.05;
    float smooth2 = 20.25;
    float smoothd = d * 20.;
    float m = smoothstep(smooth1,smooth2,smoothd);

    uv -= c;
    vec2 e = vec2(1., 1.);//sin(u+.5)*1.1415;
    float f = e.x;

    float angle = cos(atan(uv.x,uv.y))*1.0;
    float k = 0.5+sin((t+m)+u.x+angle*.15)*.5;
    O = vec4(k);
}
