#define ITR 64
#define EPS 0.001

mat2 rot(float a){
	float s=sin(a);
	float c=cos(a);
	return mat2(c,-s,s,c);
}

float sdSphere(vec3 p){
	return length(p)-0.8;
}

vec3 genNormal(vec3 p){
	vec3 normal=vec3(
		sdSphere(p+vec3(EPS,0.0,0.0))-sdSphere(p+vec3(-EPS,0.0,0.0)),
		sdSphere(p+vec3(0.0,EPS,0.0))-sdSphere(p+vec3(0.0,-EPS,0.0)),
		sdSphere(p+vec3(0.0,0.0,EPS))-sdSphere(p+vec3(0.0,0.0,-EPS))
		);
	return normalize(normal);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord){
	vec2 uv=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

	vec3 ro=vec3(0.0,0.0,-6.0);
	vec3 lookat=vec3(0.0,0.0,0.0);
	float zoom=5.0;

	vec3 f=normalize(lookat-ro);
	vec3 r=normalize(cross(vec3(0.0,1.0,0.0),f));
	vec3 u=cross(f,r);
	vec3 c=ro+f*zoom;
	vec3 i=c+uv.x*r+uv.y*u;
	vec3 rd=normalize(i-ro);

	float dS,dO;
	vec3 p,normal;

	for(int i=0;i<ITR;i++){

		p=ro+rd*dO;
		dS=sdSphere(p);
		normal=genNormal(p);
		if(dS<EPS)break;
		dO+=dS;

	}

	p.xz*=rot(iTime);
	vec3 col=vec3(0.0);

	if(dS<EPS){
		float x=atan(p.x,p.y);
		float y=length(p);

		col=vec3(x,y,1.0);
	}

	fragColor=vec4((normal*0.5-0.5)+col,1.0);
}
