void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord-iResolution.xy/2.0;
    float x=uv.x;float y=uv.y; float t = iTime;
    x=x+x*sin((degrees(atan(x,y))/20.0+t*2.0)/2.0)/3.1416/2.0;
    y=y+y*cos((degrees(atan(y,x))/20.0+t*2.0)/2.0)/3.1416/2.0;
    fragColor=vec4(0.8);
    if(abs(x)<180.0&&abs(y)<120.0) {
        if (y>40.0) fragColor=vec4(1);
        else if (y<-40.0) fragColor=vec4(1,0,0,1);
        else fragColor=vec4(0,0,1,1);
    }

}
