#define S(a, b, t) smoothstep(a, b, t)

// Flag of Jamaica
// Based on BigWIngs flag tutorial
// https://www.shadertoy.com/view/flsXRM

float PI = 3.14159;
vec3 GOLD = vec3(254, 209, 0)/255.; // PANTONE 115 
vec3 GREEN = vec3(0, 155, 58)/255.; // PANTONE 355
vec3 BLACK = vec3(0.1); // A little grey so we can see the shading

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Wavy uv
    float t = uv.x*2.-2.*iTime+uv.y*3.;
    uv.y += sin(t)*.05;

    // Default color
    vec3 col = BLACK;

    // Pixel width
    float w = fwidth(uv.x);

    // Triangles
    float tri = dot(abs(uv), vec2(-tan(PI/6.), 1.));
    tri = S(-w, w, tri);
    col = mix(BLACK, GREEN, tri);

    // Saltaire
    float stripe = abs(dot(abs(uv), vec2(-tan(PI/6.), 1.)));
    float swidth = 1./12.; // Saltaire should be one sixth of the length of the hoist
    stripe = S(swidth-w, swidth+w, stripe);
    col = mix(GOLD, col, stripe);

    // Shading 
    col *= .7+cos(t)*.3;

    // Clip top and bottom
    col *= S(w, .0, abs(uv.y)-.5);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
