// Fluid Simulation Playground
// Reference
// - Multistep Fluid Simulation by trirop https://www.shadertoy.com/view/MdSczK
// - Fluid Sandbox by riouxld https://www.shadertoy.com/view/XtfBRN
// - Dry ice by xjorma https://www.shadertoy.com/view/3d3fRB
// - Taichi Stable Fluids https://github.com/taichi-dev/taichi/blob/master/examples/simulation/stable_fluid.py

// Buffer A = Data (XY = Velocity, ZW = Attributes)
// Buffer B = Divergence
// Buffer C = Pressure
// Buffer D = Data (next step)

#define Data       iChannel0
#define Divergence iChannel1
#define Pressure   iChannel2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 data = texture(Data, uv);
    vec2 velocity = data.xy;
    vec2 payload = data.zw;

    // Visualize Velocity and Payload
    // if (false)
    {
        if (uv.y > 0.5)
            fragColor = vec4(velocity, 0.0, 1.0);
        else
            fragColor = vec4(payload, 0.0, 1.0);
    }

    // Visualize Pressure and Divergence
    if (false)
    {
        float pressure = texture(Pressure, uv).x;
        float divergence = texture(Divergence, uv).x;        

        if (uv.y > 0.5)
            fragColor = vec4(pressure, 0, 0, 0);
        else
            fragColor = vec4(divergence, -divergence, 0, 0) * 100.0;
    }
}

// Buffer A = Data (XY = Velocity, ZW = Payload)
// Buffer B = Divergence
// Buffer C = Pressure
// Buffer D = Data (next step)

#define Data iChannel3

// Advection
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - RungeKutta(fragCoord)) / iResolution.xy;
    vec4 data = texture(Data, uv);
    vec2 velocity = data.xy;
    vec2 payload = data.zw;

    // Velocity at first frame
    if (iFrame == 0)
    {
        velocity = vec2(1.0, 0.0);
    }

    // Velocity at boundary
    if (fragCoord.x < 2.0)
    {
        velocity = vec2(1.0, 0.0);
    }

    // Stripe pattern
    if (fragCoord.x < 2.0)
    {
        payload.x = fract(uv.y * 20.0) > 0.5 ? 1.0 : 0.0;
    }    

    // Block
    bool upper_block = NearPosition(iResolution.xy * vec2(0.1, 0.25), 0.01, fragCoord, iResolution);
    bool lower_block = NearPosition(iResolution.xy * vec2(0.1, 0.75), 0.01, fragCoord, iResolution);
    if (upper_block || lower_block)
    {
        velocity = vec2(0.0, 0.0);
    }

    fragColor = vec4(velocity, payload);
}

// RungeKutta 
// see https://en.wikipedia.org/wiki/List_of_Runge%E2%80%93Kutta_methods
// for more variations
#define h 2.0 // interval

vec2 RungeKutta2( vec2 fragCoord )
{
    vec2 k1 = texture(Data, (fragCoord)                 / iResolution.xy).xy;
    vec2 k2 = texture(Data, (fragCoord - h * k1)        / iResolution.xy).xy;
    return h * (k1 + k2) / 2.0;
}

vec2 RungeKutta3( vec2 fragCoord )
{
    vec2 k1 = texture(Data, (fragCoord)                 / iResolution.xy).xy;
    vec2 k2 = texture(Data, (fragCoord - 0.5 * h * k1)  / iResolution.xy).xy;
    vec2 k3 = texture(Data, (fragCoord - h * k2)        / iResolution.xy).xy;
    return h * (k1 + 4.0 * k2 + k3) / 6.0;
}

vec2 RungeKutta4( vec2 fragCoord )
{
    vec2 k1 = texture(Data, (fragCoord)                / iResolution.xy).xy;
    vec2 k2 = texture(Data, (fragCoord - 0.5 * h * k1) / iResolution.xy).xy;
    vec2 k3 = texture(Data, (fragCoord - 0.5 * h * k2) / iResolution.xy).xy;
    vec2 k4 = texture(Data, (fragCoord - h * k3)       / iResolution.xy).xy;
    return h * (k1 + 2.0 * k2 + 2.0 * k3 + k4) / 6.0;
}

// Buffer A = Data (XY = Velocity, ZW = Payload)
// Buffer B = Divergence
// Buffer C = Pressure
// Buffer D = Data (next step)

#define Data iChannel0

// Compute divergence
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float left   = texture(Data, (fragCoord + vec2(-1, 0)) / iResolution.xy).x;
    float right  = texture(Data, (fragCoord + vec2( 1, 0)) / iResolution.xy).x;
    float top    = texture(Data, (fragCoord + vec2( 0,-1)) / iResolution.xy).y;
    float bottom = texture(Data, (fragCoord + vec2( 0, 1)) / iResolution.xy).y;

    float divergence = (right - left + bottom - top) / 2.0;

    fragColor = vec4(divergence, 0, 0, 0);
}

// Buffer A = Data (XY = Velocity, ZW = Payload)
// Buffer B = Divergence
// Buffer C = Pressure
// Buffer D = Data (next step)

#define Divergence iChannel1
#define Pressure   iChannel2

// Compute pressure
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pressure = ComputePressure(fragCoord);

    fragColor = vec4(pressure, 0, 0, 0);
}

// Jacobi method to solve poisson pressure equation
float ComputePressure1( vec2 fragCoord )
{
    float pressure = 0.0;  
    float divergence = 0.0;

    pressure += texture(Pressure, (fragCoord + vec2(-1, 0)) / iResolution.xy).x * 1.0;
    pressure += texture(Pressure, (fragCoord + vec2( 1, 0)) / iResolution.xy).x * 1.0;
    pressure += texture(Pressure, (fragCoord + vec2( 0,-1)) / iResolution.xy).x * 1.0;
    pressure += texture(Pressure, (fragCoord + vec2( 0, 1)) / iResolution.xy).x * 1.0;
    divergence += texture(Divergence, fragCoord / iResolution.xy).x * 1.0;

    return (pressure - divergence) / 4.0;
}

// Allow multiple iteration in one pass
// Codegen at https://gist.github.com/silvesthu/0338b703a155c4bf61487fc0074cabf6
// Validation at https://www.shadertoy.com/view/7tlXzB

// Iterate 4 times of ComputePressure1
float ComputePressure4( vec2 fragCoord )
{
    float pressure = 0.0;
    float divergence = 0.0;

    pressure += texture(Pressure, (fragCoord + vec2(-4, 0)) / iResolution.xy).x * 1.0;
    pressure += texture(Pressure, (fragCoord + vec2(-2, 0)) / iResolution.xy).x * 16.0;
    pressure += texture(Pressure, (fragCoord + vec2(-3, -1)) / iResolution.xy).x * 4.0;
    pressure += texture(Pressure, (fragCoord + vec2(-3, 1)) / iResolution.xy).x * 4.0;
    pressure += texture(Pressure, (fragCoord + vec2(0, 0)) / iResolution.xy).x * 36.0;
    pressure += texture(Pressure, (fragCoord + vec2(-1, -1)) / iResolution.xy).x * 24.0;
    pressure += texture(Pressure, (fragCoord + vec2(-1, 1)) / iResolution.xy).x * 24.0;
    pressure += texture(Pressure, (fragCoord + vec2(-2, -2)) / iResolution.xy).x * 6.0;
    pressure += texture(Pressure, (fragCoord + vec2(-2, 2)) / iResolution.xy).x * 6.0;
    pressure += texture(Pressure, (fragCoord + vec2(2, 0)) / iResolution.xy).x * 16.0;
    pressure += texture(Pressure, (fragCoord + vec2(1, -1)) / iResolution.xy).x * 24.0;
    pressure += texture(Pressure, (fragCoord + vec2(1, 1)) / iResolution.xy).x * 24.0;
    pressure += texture(Pressure, (fragCoord + vec2(0, -2)) / iResolution.xy).x * 16.0;
    pressure += texture(Pressure, (fragCoord + vec2(0, 2)) / iResolution.xy).x * 16.0;
    pressure += texture(Pressure, (fragCoord + vec2(-1, -3)) / iResolution.xy).x * 4.0;
    pressure += texture(Pressure, (fragCoord + vec2(-1, 3)) / iResolution.xy).x * 4.0;
    pressure += texture(Pressure, (fragCoord + vec2(4, 0)) / iResolution.xy).x * 1.0;
    pressure += texture(Pressure, (fragCoord + vec2(3, -1)) / iResolution.xy).x * 4.0;
    pressure += texture(Pressure, (fragCoord + vec2(3, 1)) / iResolution.xy).x * 4.0;
    pressure += texture(Pressure, (fragCoord + vec2(2, -2)) / iResolution.xy).x * 6.0;
    pressure += texture(Pressure, (fragCoord + vec2(2, 2)) / iResolution.xy).x * 6.0;
    pressure += texture(Pressure, (fragCoord + vec2(1, -3)) / iResolution.xy).x * 4.0;
    pressure += texture(Pressure, (fragCoord + vec2(1, 3)) / iResolution.xy).x * 4.0;
    pressure += texture(Pressure, (fragCoord + vec2(0, -4)) / iResolution.xy).x * 1.0;
    pressure += texture(Pressure, (fragCoord + vec2(0, 4)) / iResolution.xy).x * 1.0;
    divergence += texture(Divergence, (fragCoord + vec2(-3, 0)) / iResolution.xy).x * 1.0;
    divergence += texture(Divergence, (fragCoord + vec2(-1, 0)) / iResolution.xy).x * 9.0;
    divergence += texture(Divergence, (fragCoord + vec2(-2, -1)) / iResolution.xy).x * 3.0;
    divergence += texture(Divergence, (fragCoord + vec2(-2, 1)) / iResolution.xy).x * 3.0;
    divergence += texture(Divergence, (fragCoord + vec2(-2, 0)) / iResolution.xy).x * 4.0;
    divergence += texture(Divergence, (fragCoord + vec2(1, 0)) / iResolution.xy).x * 25.0;
    divergence += texture(Divergence, (fragCoord + vec2(0, -1)) / iResolution.xy).x * 25.0;
    divergence += texture(Divergence, (fragCoord + vec2(0, 1)) / iResolution.xy).x * 25.0;
    divergence += texture(Divergence, (fragCoord + vec2(0, 0)) / iResolution.xy).x * 16.0;
    divergence += texture(Divergence, (fragCoord + vec2(-1, -2)) / iResolution.xy).x * 3.0;
    divergence += texture(Divergence, (fragCoord + vec2(-1, -1)) / iResolution.xy).x * 8.0;
    divergence += texture(Divergence, (fragCoord + vec2(-1, 2)) / iResolution.xy).x * 3.0;
    divergence += texture(Divergence, (fragCoord + vec2(-1, 1)) / iResolution.xy).x * 8.0;
    divergence += texture(Divergence, (fragCoord + vec2(-1, 0)) / iResolution.xy).x * 16.0;
    divergence += texture(Divergence, (fragCoord + vec2(3, 0)) / iResolution.xy).x * 1.0;
    divergence += texture(Divergence, (fragCoord + vec2(2, -1)) / iResolution.xy).x * 3.0;
    divergence += texture(Divergence, (fragCoord + vec2(2, 1)) / iResolution.xy).x * 3.0;
    divergence += texture(Divergence, (fragCoord + vec2(2, 0)) / iResolution.xy).x * 4.0;
    divergence += texture(Divergence, (fragCoord + vec2(1, -2)) / iResolution.xy).x * 3.0;
    divergence += texture(Divergence, (fragCoord + vec2(1, -1)) / iResolution.xy).x * 8.0;
    divergence += texture(Divergence, (fragCoord + vec2(1, 2)) / iResolution.xy).x * 3.0;
    divergence += texture(Divergence, (fragCoord + vec2(1, 1)) / iResolution.xy).x * 8.0;
    divergence += texture(Divergence, (fragCoord + vec2(0, -3)) / iResolution.xy).x * 1.0;
    divergence += texture(Divergence, (fragCoord + vec2(0, -2)) / iResolution.xy).x * 4.0;
    divergence += texture(Divergence, (fragCoord + vec2(0, 3)) / iResolution.xy).x * 1.0;
    divergence += texture(Divergence, (fragCoord + vec2(0, 2)) / iResolution.xy).x * 4.0;
    divergence += texture(Divergence, (fragCoord + vec2(0, 0)) / iResolution.xy).x * 64.0;

    return (pressure - divergence) / 256.0;
}

// Iterate 20 times of ComputePressure1
float ComputePressure20( vec2 fragCoord )
