// Buffer A = Data (XY = Velocity, ZW = Payload)
// Buffer B = Divergence
// Buffer C = Pressure
// Buffer D = Data (next step)

#define Data     iChannel0
#define Pressure iChannel2

// Substract pressure gradient
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float left   = texture(Pressure, (fragCoord + vec2(-1, 0)) / iResolution.xy).x;
    float right  = texture(Pressure, (fragCoord + vec2( 1, 0)) / iResolution.xy).x;
    float top    = texture(Pressure, (fragCoord + vec2( 0,-1)) / iResolution.xy).x;
    float bottom = texture(Pressure, (fragCoord + vec2( 0, 1)) / iResolution.xy).x;

    vec2 gradient = vec2(right - left, bottom - top) / 2.0;

    vec4 data = texture(Data, fragCoord / iResolution.xy);
    vec2 velocity = data.xy;
    vec2 payload = data.zw;

    // Based on Helmholtz-Hodge Decomposition
    // Substract gradient of pressure to get a divergence-free vector field
    fragColor = vec4(velocity - gradient, payload);
}

bool NearPosition( vec2 targetCoords, float radius, vec2 fragCoord, vec3 iResolution )
{
    vec2 vector = (targetCoords - fragCoord) / iResolution.y;
    return length(vector) < radius;
}

