void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 //Pause if mouse is down
  if(iMouse.z > 0.) {
  fragColor = texelFetch(iChannel0,ivec2(fragCoord),0);
  return;}

  //Count neighbors
  int neighborCount = 0;
  for(int adj_col = -1; adj_col <= 1; adj_col++){
    for(int adj_row = -1; adj_row <= 1; adj_row++){
    if(adj_col == 0 && adj_row==0) continue;
     if(texelFetch(iChannel0, ivec2(fragCoord.xy - vec2(adj_col,adj_row)),0) == vec4(1)){
     neighborCount++;
     }
    }
  }

  //Adjust state as appropriate
  switch(neighborCount){
  case 8:
  fragColor = vec4(1);
  break;
  case 3:
  fragColor = vec4(1);
  break;
  case 2:
  if(texelFetch(iChannel0,ivec2(fragCoord.xy),0) == vec4(1)){
      fragColor = vec4(1);
  } else {fragColor = vec4(0);}
  break;
  default:
  fragColor = vec4(0);
  }


}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  if (iFrame == 0 && fragCoord == vec2(0)) {
      fragColor = vec4(iTime);
      return;
  }
  if(iTime - texelFetch(iChannel0, ivec2(0),0).r < 1.5) {
  vec2 size = vec2(textureSize(iChannel1,0));
  if(rand(fragCoord + iMouse.xy) > 0.5) {
  fragColor = vec4(1);
  }
  //fragColor = vec4(texelFetch(iChannel1, ivec2(mod(fragCoord.xy/2.,size.xy)), 0).r);
  return;
  }



  ivec2 dist = ivec2(abs(fragCoord.xy - iMouse.xy));
  if(dist.x <= 1 && dist.y <= 1){
      fragColor = vec4(1);
  } else {
    fragColor = texelFetch(iChannel0, ivec2(fragCoord.xy),0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    //Gravity warp
       vec2 gravity = vec2(0.,0.);
  //Gravity Calculation
 for(int adj_col = -3; adj_col <= 3; adj_col++){
    for(int adj_row = -3; adj_row <= 3; adj_row++){
    if(adj_col == 0 && adj_row==0) continue;
    if(texelFetch(iChannel0,ivec2(fragCoord+vec2(adj_col,adj_row)),0) == vec4(1.))
       gravity += vec2(adj_col,adj_row) * 1e5;
    }
  }
  if(1./gravity.x < 1e-6 || 1./gravity.y < 1e-6) {
  fragColor = texelFetch(iChannel0,ivec2(fragCoord),0); return;
  }
  //fragColor = vec4(ceil(1./gravity.x),ceil(1./gravity.y),1,1) * texelFetch(iChannel0,ivec2(fragCoord),0);
  fragColor = texelFetch(iChannel0,ivec2(fragCoord-ceil(1./gravity)),0);
}
