const float PI = 3.14;

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.xy;
    float y = 0.1 * sin(8. * U.x + 4. * sin(iTime)) * cos(iTime);
    float at = sqrt(1. - U.x * U.x);
    O = vec4(smoothstep(3./Res.y, 0., abs(U.y - y * at)));
}
