#define NoiseStrength 2.0
#define NoiseFinalMultiplier 1.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float sint = sin(iTime) * 0.5 + 0.5;
    float cost = cos(iTime) * 0.5 + 0.5;

    vec4 p0 = texture(iChannel0 ,uv);
    vec4 p1 = texture(iChannel0 ,uv + vec2(0.25 ,0.25));
    vec4 r0 = mix(p0 ,p1 ,sint);

    vec4 p2 = texture(iChannel0 ,uv + vec2(0.50 ,0.50));
    vec4 p3 = texture(iChannel0 ,uv + vec2(0.75 ,0.75));
    vec4 r1 = mix(p2 ,p3 ,cost);

    float n = 1.0 - max(r0.x ,r1.x);

    n = clamp((1.0 - n * NoiseStrength) * NoiseFinalMultiplier ,0.0 ,1.0);

    // Output to screen
    fragColor = vec4(vec3(n,n,n),1.0);
}
