void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // 得到 UV
    vec2 uv = fragCoord/iResolution.xy;
    // 偏移 x 使之处于屏幕中央且宽高相等
    uv.x = uv.x * iResolution.x / iResolution.y - 0.438f;

    // 半径
    float radius = 0.5f;

    // 圆
    float circle = 1.f - (distance(uv, vec2(0.5, 0.5)) + (1.f - clamp(radius, 0.f, 1.f)));

    // 笔刷硬度
    float density = 100.f;

    circle = circle * density;

    // 输出值和分段的积
    fragColor = vec4(circle);
}
