void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float sinX = (sin(uv.x * 13. - iTime * 5.) / 6.) + .2;
    float sinY = (sin(uv.y * 4. - iTime * 5.) / 6.) + .2;

    float g = 
    smoothstep(sinX - .01, sinX, uv.y) - 
    smoothstep(sinX, sinX + .01, uv.y);

    float b = 
    smoothstep(sinY - .01, sinY, uv.x) -
    smoothstep(sinY, sinY + .01, uv.x);

    vec3 col = vec3(1.0, 0.0f, 0.0f)*(g + b);

    // Output to screen
    fragColor = vec4(col, 1.0);
}
