void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = fragCoord/iResolution.xy;
    st.x *= 1.;

    vec3 color = vec3(0.);
    float r = length(st-vec2(0.5, 0.)) + (1.8+3.*abs(sin(.3*iTime)))*abs(st.y);

    color = mix(vec3(0.965,1.000,0.016), vec3(0.163+abs(.7*sin(.3*iTime)),0.797,1.000), r+.2*abs(sin(.3*iTime)));

    fragColor = vec4(color,1.0);
}
