vec3 rule(float time)
{
    vec3 rule;
    rule.x = clamp((0.3334 - time) * 3.0, 0.0, 1.0);
    rule.z = clamp((time - 0.6666) * 3.0, 0.0, 1.0);
    rule.y = clamp(1.0 - rule.x - rule.z, 0.0, 1.0);
    return rule;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
	vec4 src = texture(iChannel0, uv);
    vec4 dst = texture(iChannel1, uv);
    vec4 black = vec4(0.0);
    vec3 rule = rule(mod(iTime, 2.0) - 0.25);
    fragColor = src * rule.x + black * rule.y + dst * rule.z;
}
