// looped every 20 seconds

vec3 col1 = vec3(0.094, 0.094, 0.094);
vec3 col2 = vec3(0.529, 0.345, 1);
vec3 col3 = vec3(0.361, 0.722, 0.894);
vec3 col4 = vec3(0.949, 0.949, 0.949);
const float PI = 3.14159265358;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xx * 45.;
    float t = iTime * .05;
    float v = uv.y + uv.x;
    float vf = fract(v / 2. + sin(uv.x + uv.y + t * PI * 2.));
    float dist = vf - 0.5;
    float c_part = float(vf < .9);
    vec3 col = vec3(mix(col3, col1, c_part + dist * float(dist < 0.2)));
    fragColor = vec4(col * .5,1.);
}
