vec3 sunColor1 = vec3 (1.0,1.0,0.0);
vec3 sunColor2 = vec3 (1.0,0.5,0.0);
vec3 background1 = vec3(0.1,0.1,0.9);
vec3 background2 = vec3(0.0,0.5,1.0);
vec3 foregroundColor = vec3(0.0,0.4,0.0);
float radius = .25;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
// Normalized pixel coordinates (from 0 to 1)
vec2 uv = fragCoord/iResolution.xy;

//Create the sun gradient and background gradient
vec3 sunGrad = mix(sunColor2,sunColor1, uv.y);
vec3 backGrad = mix(background1, background2,uv.y);

//Creates the circle and centers it correctly.
float aspRatio = iResolution.x/iResolution.y; //1.7777
uv.x = uv.x*aspRatio;
vec2 diff= uv - vec2(.5*aspRatio,cos(iTime)*0.5);
float sun = length(diff);
 sun= step(sun,radius);

//Combines the two previous gradients
vec3 col = mix(backGrad,sunGrad, sun);

//Create foreground
float mountain = sin(uv.y);
float mountainPos= step(mountain,0.1);
vec3 fore = foregroundColor;

//Combine all colors
vec3 col1 =mix(col,fore,mountainPos);;



// Output to screen
fragColor = vec4(col1, 1.0);
}
