void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    vec4 a = texture(iChannel0,u);
    fragColor = a.z+a.z*sin(length(a.xy)+vec4(1,2,3,4)+0.);
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4  r = vec4(0);
    vec4  a = A(u);
    float z    = 8.;//kernel convolution size
    float blur = 3./z;
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        vec2  c = vec2(i,j)*blur; //c = c.yx*vec2(-1,1);
              c*= exp(-dot(c,c));
        vec4  a2= A(u+vec2(i,j));
        vec4  b = a2-a;
        r.xy += c*b.z;
        r.z  += dot(c,b.xy           )*a2.z;
        r.w  += dot(c,b.yx*vec2(-1,1))*a2.z;
    }}
    fragColor = r;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4  r = A(u);
    float z    = 8.;//kernel convolution size
    float blur = 3./z;
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
      vec4 b = B(u+vec2(i,j));
      vec2 c = (-vec2(i,j))*blur;
           c*= exp(-dot(c,c))*.06*r.z;
      r.xy += +b.z*c              *0. //enhance divergence
              +b.w*c.yx*vec2(-1,1)*1. //enhance curl 
              +abs(b.z)*(step(0.,b.w)*2.-1.)*c.yx*vec2(-1,1)*0.;
    }}

    fragColor = r;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
#define C(u) texture(iChannel2,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 t = A(u);
    vec2 m = +t.xy
             +B(u).xy*(t.z-.5)
             +t.z*vec2(0,.0)
             -C(u).x*t.xy*.0;
    float s = 0.;
    float z    = 8.;//kernel convolution size
    float blur = 4./z;
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
      vec2 c = (m+vec2(i,j))*blur;
      s += exp(-dot(c,c));
    }}
    if(s==0.){s = 1.;}
    s = 1./s;

    fragColor = vec4(m,s,0);
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float tz = 0.;
    vec4 a = vec4(0);
    float z    = 8.;//kernel convolution size
    float blur = 4./z;
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
      vec4 t = A(u+vec2(i,j));
      vec4 m = B(u+vec2(i,j));
      vec2 c = (m.xy-vec2(i,j))*blur;
      float z = t.z*exp(-dot(c,c));
      a.xy += z*m.xy;
      a.z  += z*m.z;
      tz   += z;
    }}
    if(tz==0.){tz = 1.;}
    a.xy /= tz;
    if(iMouse.z>0.)
    {
        vec2 m = 16.*(u-iMouse.xy)/iResolution.y;
        a += vec4(1,0,1,1)*.1*exp(-dot(m,m));
    }
    if(iFrame==0)
    {
        vec2 m = 4.*(u-iResolution.xy*.5)/iResolution.y;
        a = vec4(1,0,1,1)*exp(-dot(m,m));
    }
    fragColor = a;
}
