#define PI 3.141592654f
vec2 pixelate_tex(vec2 uv, int factor)
{
    float   inv_ar  = iResolution.y / iResolution.x;

    vec2    fact_uv = vec2(float(factor), floor(inv_ar*float(factor)));
    return (floor(uv / iResolution.xy * fact_uv) + vec2(0.5f,0.5f)) / fact_uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = pixelate_tex(fragCoord + (40.f*vec2(float(iTime))), max(int(1920.f * (0.5f + 0.5f*sin(PI*iTime))), 1));
    vec3 col = vec3(texture(iChannel0, uv));
    fragColor = vec4(col,1.0);
}
