/**  // 212 chars  ( see golfing below )

void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y /.1; 
    float t = mod(iTime,2.),

          z = t < 1.           ? exp2(fract(iTime))     // phase1: expend
            : mod(U.x,2.) > 1. ? U.y -=  (2.-t)/.1 , 1. // phase2: insert fall
            :                       2. ;
    U.x = fract(U.x/z) * z - .5;                        // lateral expend

    O = 1.- (length(U)-.47) *R.yyyy/30.;                // draw discs

}
