/**/ // 194:  -7 by Xor

void mainImage( out vec4 O, vec2 U )
{
    U += U - (O.xy=iResolution.xy);
    U /= O.y * .1;
    float t = mod(iTime,2.);
    U.x = mod( U,  t  < 1.          ? exp2(fract(t))
                 : mod(U.x,2.) > 1. ? U.y -=  20.-t/.1, 1.
                 :                    2. 
             ).x - .5;

    O = (.55-length(U)) * O.yyyy/30.;
}
