/*

	Fluid dynamics controls velocity field.
	Particles translate with the velocity field.
	Particles reproduce as they move. 
	Particles diffuse  4  hormones. 
	Diffusion is mediated by the diffusinon equation and fluid dynamics
	Particles experience a force from each hormone.
	The force is proportional to their own hormone signature. 
	Each hormone diffuses with a different radius. 
	Each initial particle has its own hormone signature.
	Particles metamorphosize when hormone levels are high
	Then they battle it out! 

*/
void mainImage( out vec4 Q, in vec2 U)
{
    if(iMouse.z>0.) {
        U -= iMouse.xy;
        U*=.5;
        U += iMouse.xy;
    }
    vec4 b = B(U);
    vec4 h = (hash(b.w));
    Q = smoothstep(2.1,0.,length(b.xy-U))*(.5+2.*h);
	Q += C(U).yzwx;
}

// FLUID DYNAMICS
// FORCE on FLUID = (PARTICLE)*(GRADIENT OF BUFFER D)

#define R iResolution.xy
#define A(U) texture(iChannel0, (U)/R)
#define B(U) texture(iChannel1, (U)/R)
vec4 T (vec2 U) {return A(U-A(U).xy);}
void mainImage( out vec4 Q, in vec2 U)
{
    Q = T(U);
    vec4 // neighborhood
        n = T(U+vec2(0,1)),
        e = T(U+vec2(1,0)),
        s = T(U-vec2(0,1)),
        w = T(U-vec2(1,0));
   // FLUID DYNAMICS
   //Q.x -= (0.249999*(e.z-w.z-Q.w*(n.w-s.w)));
   //Q.y -= (0.249999*(n.z-s.z-Q.w*(e.w-w.w)));
   //Q.z += (0.2500001*((s.y-n.y+w.x-e.x)+(n.z+e.z+s.z+w.z))-Q.z);
   //Q.w += (0.25000001*(s.x-n.x+w.y-e.y)-Q.w);

   // ORIGINAL
   Q.x -= (0.249999*(e.z-w.z-Q.w*(n.w-s.w)));
   Q.y -= (0.249999*(n.z-s.z-Q.w*(e.w-w.w)));
   Q.z += (0.249999*((s.y-n.y+w.x-e.x)+(n.z+e.z+s.z+w.z))-Q.z);
   Q.w += (0.249999*(s.x-n.x+w.y-e.y)-Q.w);
   Q.xy *= 0.99995;
   //Q.xy *= 1.0000000001;
   // COMPUTE HORMONE FEILD
   n = C(U+vec2(0,1));
   e = C(U+vec2(1,0));
   s = C(U-vec2(0,1));
   w = C(U-vec2(1,0));
   // THIS PARTICLE
   vec4 b = B(U);
   // COMPUTE HORMONE SIGNATURE
   vec4 h = hash(b.w);
   // SUM HORMONE FORCE
   vec2 v = vec2(0);
   v += h.x*vec2(e.x-w.x,n.x-s.x);
   v += h.y*vec2(e.y-w.y,n.y-s.y);
   v += h.z*vec2(e.z-w.z,n.z-s.z);
   v += h.w*vec2(e.w-w.w,n.w-s.w);
   // APPLY HORMONE FORCE TO THIS PARTICLE
 //  Q.xy += v*smoothstep(0.80005,0.1,length(U-b.xy));
   Q.xy += v*smoothstep(1.401,0.0009,length(U-b.xy));
   // BOUNDARY CONDITIONS
   if (U.x<1.||U.y<1.||R.x-U.x<1.||R.y-U.y<1.||iFrame<1)
       Q.xyw = vec3(0);

}

// SPACIALLY SORT VORONOI PARTICLES
// ALLOW MOVING PARTICLES TO LEAVE A TRAIL OF CLONES
void swap (inout vec4 Q, vec2 U, vec2 r) {
	vec4 n = B(U+r);
    if (length(U-n.xy)<length(U-Q.xy)) Q = n;
}
void mainImage( out vec4 Q, in vec2 U)
{
    // FIND NEAREST PARTICLE
    Q = B(U);
    swap(Q,U,vec2(1,0));
    swap(Q,U,vec2(0,1));
    swap(Q,U,vec2(-1,0));
    swap(Q,U,vec2(0,-1));
    swap(Q,U,vec2(2,2));
    swap(Q,U,vec2(2,-2));
    swap(Q,U,vec2(-2,2));
    swap(Q,U,vec2(-2,-2));
    if (length(hash(Q.w)-C(U))<.1)Q.w=dot(hash(Q.w),vec4(1e3));
    // LEAVE A TRIAL OF CLONES AS PARTICLE TRANSLATES
    Q.xy += A(mix(U,Q.xy,.7)).xy;
    // BOUNDARY CONDITIONS
    if ((iMouse.z>0.&&length(iMouse.xy-U)<30.)||iFrame < 1) {
    	Q = vec4(U,0,0);
        Q.w = .1*(Q.x+R.x*Q.y+dot(iDate,vec4(1)));
    }

}

//Diffusion on fluid
void mainImage( out vec4 Q, in vec2 U )
{
    vec4 b = B(U), a= A(U);
    U -= .5*a.xy;
    Q = D(U);
    vec4 
        n = D(U+vec2(0,1)),
        e = D(U+vec2(1,0)),
        s = D(U-vec2(0,1)),
        w = D(U-vec2(1,0));
    Q = mix(Q,0.25*(n+e+s+w),.2*vec4(2,3,4,5));
    vec4 h = hash(b.w);
    Q += .1*(h-Q)*smoothstep(1.,0.,length(b.xy-U));

    if (iFrame < 1) Q = vec4(0);
}

#define R iResolution.xy
#define A(U) texture(iChannel0, (U)/R)
#define B(U) texture(iChannel1, (U)/R)
#define C(U) texture(iChannel2, (U)/R)
#define D(U) texture(iChannel3, (U)/R)


#define S vec4(2,4,6,8)
#define M .5*vec4(4,3,2,1)
#define O .5/S/S
#define I 140.

vec4 hash (float p) // Dave (Hash)kins
{
	vec4 p4 = fract(vec4(p) * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+19.19);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx)*2.-1.;

}

//Diffusion on fluid
void mainImage( out vec4 Q, in vec2 U )
{
    vec4 b = B(U), a= A(U);
    U -= .5*a.xy;
    Q = C(U);
    vec4 
        n = C(U+vec2(0,1)),
        e = C(U+vec2(1,0)),
        s = C(U-vec2(0,1)),
        w = C(U-vec2(1,0));
    Q = mix(Q,0.25*(n+e+s+w),.2*vec4(2,3,4,5));
    vec4 h = hash(b.w);
    Q += .1*(h-Q)*smoothstep(1.,0.,length(b.xy-U));

    if (iFrame < 1) Q = vec4(0);
}
