#define DARK_BLUE vec3(0.004,0.129,0.412)
#define RED vec3(0.784,0.063,0.18)
#define WHITE vec3(1.)


float deg2rad(float deg){
    return deg * 3.14159265359/180.;
}

mat2 rotate(float angle){
	return mat2( cos(angle), -sin(angle), sin(angle), cos(angle));
}


float whiteCross(vec2 uv)
{
    float mask = 0.;
    mask =  step(-0.15,uv.x) - step(.15, uv.x);
    mask += step(-0.14,uv.y) - step(.14, uv.y);
    return mask; 
}

float redCross(vec2 uv)
{
    float mask = 0.;
    float maskh = step(-0.09,uv.x) - step(0.09, uv.x);
    float maskv = step(-0.09,uv.y) - step(0.09, uv.y); 
    mask = mix(maskh,maskv,maskv);
    return mask;
}

float redBands(vec2 uv, float width)
{
   float mask1 = 0.;
   float mask2 = 0.;

   vec2 luv = uv * rotate(deg2rad(60.));

   if(uv.y > 0.14)
   {
       mask1 = step(-width,luv.x) - step(0.,luv.x);
   }
   if(uv.y < -0.14){
       mask1 = step(0.,luv.x) - step(width,luv.x);
   }

    luv = uv * rotate(deg2rad(117.));

   if(uv.y > 0.14)
   {
       mask2 = step(-width,luv.x-width) - step(0.,luv.x-width);
   }
   if(uv.y < -.14){
       mask2 = step(0.,luv.x+width) - step(width,luv.x+width);
   }

   return mix(mask1, mask2, mask2);
}


float whiteBand(vec2 uv)
{
    float mask=0.;

    vec2 luv = uv * rotate(deg2rad(60.));
    float mask1 = step(-0.08, luv.x) - step(.08, luv.x);

    luv = uv * rotate(deg2rad(-63.));
    float mask2 = step(-0.08, luv.x) - step(.08, luv.x);

    mask = mask1 + mask2;
    return  mask;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;

    // background 
    vec3 col = vec3(DARK_BLUE);

    col = mix( col, WHITE, whiteCross(uv));
    col = mix( col, WHITE, whiteBand(uv) );
    col = mix( col, RED, redCross(uv));    
    col = mix( col, RED, redBands(uv, 0.05));


    // Output to screen
    fragColor = vec4(col,1.0);
}
