const float offset = 0.03;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 forground = texture(iChannel0, uv);
    vec4 forground_copy_1 = texture(iChannel0, uv + vec2(-offset, 0.0));
    vec4 forground_copy_2 = texture(iChannel0, uv + vec2(-2.0 * offset, 0.0));
    vec4 forground_copy_3 = texture(iChannel0, uv + vec2(-3.0 * offset, 0.0));
    vec4 background = texture(iChannel1, uv);

    forground_copy_1.rgb = vec3(145.0/255.0, 252.0/255.0, 245.0/255.0);
    forground_copy_2.rgb = vec3(225.0/255.0,  90.0/255.0, 170.0/255.0);
    forground_copy_3.rgb = vec3(245.0/255.0, 250.0/255.0, 108.0/255.0);



    forground_copy_3 = mix(forground_copy_3, forground_copy_2, forground_copy_2.a);
    forground_copy_3 = mix(forground_copy_3, forground_copy_1, forground_copy_1.a);
    forground_copy_3 = mix(forground_copy_3, forground, forground.a);

    /*    
    if (forground_copy_2.a > 0.0)
        forground_copy_3 = forground_copy_2;
    if (forground_copy_1.a > 0.0)
        forground_copy_3 = forground_copy_1;
    if (forground.a > 0.0)
        forground_copy_3 = forground;
    */


    fragColor = mix(background, forground_copy_3, forground_copy_3.a);
}

float th = 20.0; // threshold
float a2 = 1.2;

float getAlpha(vec4 c){
	return 1.0 - th*(c.g-a2*(max(c.r,c.b)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec4 fg = texture(iChannel0,uv);

    float alpha = step(0.5, getAlpha(fg));

	fragColor = vec4(fg.rgb, alpha);
}
