//#define STEREOGRAPHIC

vec3 star_func(float br, float R, float ci){
    float conv = 4.*exp(-600.*max(0.003,R)) + 0.01*exp(-50.*R) + 0.001*exp(-20.*R);;


    vec3 star_color = blackbody_linsrgb(BV_to_temp(ci));
    star_color = mix(vec3(1.),star_color, br/(.1+br));

    vec3 col = vec3(10.)*conv*vec3(pow(br,.75));
    return star_color*col*col;
}

vec3 camera_stereographic(vec2 ss){
    float stereo_denom = 1./(1.+dot(ss,ss));
    vec3 rd = vec3(2.*ss.x, 2.*ss.y, -2.)*stereo_denom;
    rd.z += 1.;
    return rd;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = fragCoord/iResolution.xy;

    float fov = 60.*DEG;

    vec2 ss = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    vec3 ro = texelFetch(iChannel3, ivec2(0,0), 0).xyz;

    vec4 camera_angle_texel = texelFetch(iChannel3, ivec2(1,0), 0);
    float pitch = camera_angle_texel.y;
    float yaw = camera_angle_texel.x;

    vec3 right = vec3(sin(yaw), -cos(yaw), 0.0);
    vec3 up = vec3(-cos(yaw)*sin(pitch), -sin(yaw)*sin(pitch), cos(pitch));
    vec3 forward = vec3(cos(yaw)*cos(pitch), sin(yaw)*cos(pitch), sin(pitch));

    #ifdef STEREOGRAPHIC
    vec3 rd = camera_stereographic(ss * (fov)/PI);
    #else
    vec3 rd = normalize(vec3(ss.x,ss.y,-1.0/tan(.5*fov)));
    #endif

    mat3 camera_matrix = mat3(right,up,-forward);

    rd = camera_matrix*rd;

    vec3 col;


    for(int i = 0; i < 1024; i++){


        uint enc1 = compressed(2*i+0);
        uint enc2 = compressed(2*i+1);

        float x = 1024.*((float(enc1&0xFFFFu)/32768.)*2.-1.);
        float y = 1024.*((float((enc1>>16)&0xFFFFu)/32768.)*2.-1.);
        float z = 1024.*((float(enc2&0xFFFFu)/32768.)*2.-1.);
        float absmag = 20.*((float((enc2>>16)&0xFFu)/256.)*2.-1.);
        float ci = (float((enc2>>24)&0xFFu)/256.)*5.95-0.45;

        vec3 star_pos = vec3(x,y,z);
        vec3 to_star = star_pos-ro;
        vec3 to_star_n = normalize(to_star);
        if(dot(to_star_n,rd) > 0.9){
            float br = exp2(-1.3287712379549448*absmag);
            br /= dot(to_star,to_star);
            br *= 100.;

            to_star_n = inverse(camera_matrix)*to_star_n;

            #ifdef STEREOGRAPHIC
            vec2 projected = PI*to_star_n.xy/(2.*(-to_star_n.z+1.)*fov);
            #else
            vec2 projected = -to_star_n.xy/(2.*tan(.5*fov)*to_star_n.z);
            #endif

            float R = (iResolution.y/360.)*distance(projected,ss);




            col += star_func(br, R, ci);
        }
    }

    //col = tanh(col*3.);

    //col *= 0.5;
    //col += 5.1*vec3(0.001,0.003,0.01);

    /*float k = rd.z*rd.z;
    k = 1.-k;
    k *= k;
    k *= k;
    k *= k;
    k *= k;
    col += vec3(0.05*k);*/

    col = pow(col, vec3(1./2.2));



    fragColor = vec4(col,1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    //W = 87
    //S = 83
    //D = 68
    //A = 65
    //space = 32
    //shift = 16

    //0,0 = camera pos
    //1,0 = camera_euler

    bool is_start = iFrame == 0;

    ivec2 fc = ivec2(fragCoord);
    vec4 cv = texelFetch(iChannel0, fc, 0);
    vec4 nv = cv;
    if(fc == ivec2(0,0)){ //Camera motion

        if(is_start){ //Init
            fragColor = vec4(0.0, 0.0, 0., 0.0);
            return;
        }

        vec4 camera_angle_texel = texelFetch(iChannel0, ivec2(1,0), 0); //Geometry
        float pitch = camera_angle_texel.y;
        float yaw = camera_angle_texel.x;

        vec3 off;

        float W = texelFetch(iChannel1, ivec2(87, 0), 0).x;
        float S = texelFetch(iChannel1, ivec2(83, 0), 0).x;
        float A = texelFetch(iChannel1, ivec2(65, 0), 0).x;
        float D = texelFetch(iChannel1, ivec2(68, 0), 0).x;
        float space = texelFetch(iChannel1, ivec2(32, 0), 0).x;
        float shift = texelFetch(iChannel1, ivec2(16, 0), 0).x;

        float F = texelFetch(iChannel1, ivec2(70, 0), 0).x;

        vec3 hor_forward = vec3(cos(yaw), sin(yaw), 0.0); 
        vec3 hor_right = vec3(sin(yaw), -cos(yaw), 0.0);

        float spd = .2*length(cv)+1.;


        if(F > 0.0) spd *= 2.0;
        if(W > 0.0){
            nv.xyz += spd*iTimeDelta*hor_forward;
        }
        if(S > 0.0){
           nv.xyz -= spd*iTimeDelta*hor_forward;
        }
        if(A > 0.0){
            nv.xyz -= spd*iTimeDelta*hor_right;
        }
        if(D > 0.0){
            nv.xyz += spd*iTimeDelta*hor_right;
        }
        if(space > 0.0){
            nv.z += spd*iTimeDelta;
        }
        if(shift > 0.0){
            nv.z -= spd*iTimeDelta;
        }
    }

    //0 - increment per-frame, smoother
    //1 - increment relative to last click, time-stable but stutters

    int mouse_mode = 0;


    if(fc == ivec2(1,0)){
        if(is_start){
            fragColor = vec4(0.0, -1.4, 0.0, 0.0);
            return;
        }
        vec4 old_mouse = texelFetch(iChannel0, ivec2(2,0), 0);
        vec4 anchor = texelFetch(iChannel0, ivec2(2,1), 0);

        if(old_mouse.z > 0.0){
            if(mouse_mode == 0){
                nv.xy = cv.xy - 1.5*vec2(1.0,-1.0)*(iMouse.xy - old_mouse.xy)/iResolution.y;
                nv.x = mod(nv.x, 2.0*PI);
                nv.y = clamp(nv.y, -0.5*PI, 0.5*PI);
            }
            if(mouse_mode == 1){
                nv.xy = anchor.xy + 0.01*vec2(-1.0, 1.0)*(iMouse.xy - old_mouse.xy);
            }

        }

    }
    if(fc == ivec2(2,0)){
        if(mouse_mode == 0){
            nv = iMouse;
        }
        if(mouse_mode == 1){
            if(iMouse.z != cv.z) nv = iMouse;
        }
    }
    if(fc == ivec2(2,1)){
        if(mouse_mode == 1){
            vec4 old_mouse = texelFetch(iChannel0, ivec2(2,0), 0);
            if(iMouse.z != old_mouse.z) nv = texelFetch(iChannel0, ivec2(1,0), 0);
        }
    }

    fragColor = nv;


}
