#define PI 3.1415926535

float random (vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = 70. * fragCoord/iResolution.y;

    float N = 2.0;

    float row = floor(st.y);
    st.x += 50.*iTime*sin(2.*PI*random(vec2(row+1., row)));

    st = floor(st);

    //float col = step(iMouse.y/iResolution.y*0.5+iMouse.x/iResolution.x*0.5, random(st));
    vec2 M;
    if(iMouse.xy==vec2(0))
    {
        M = vec2(sin(iTime*0.5)*0.25+0.25);
    }
    else{
        M = iMouse.xy/iResolution.xy*0.5;
    }
    float col = step(M.x+M.y, random(st));
    fragColor = vec4(vec3(col),1.0);
}
