void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 color1 = texture(iChannel0,uv);
    float num = -2.0 * mod(iTime,1.0);
    float time1 = 1.0 - pow(2.0, num);
    if (uv.x > 0.0 + time1 && uv.x < 0.2 + time1) {
        uv.x += 0.04;
        color1 = texture(iChannel0,uv);
    }

    vec4 color2 = texture(iChannel0,uv);
    float time2 = time1-0.05;
    if (uv.x > 0.0 + time2 && uv.x < 0.2 + time2) {
        uv.x += 0.09;
        color2 = texture(iChannel0,uv);
    }

    vec4 color3 = texture(iChannel0,uv);
    float time3 = time2-0.02;
    if (uv.x > 0.0 + time3 && uv.x < 0.2 + time3) {
        uv.x += 0.10;
        color3 = texture(iChannel0,uv);
    }

    fragColor = mix(mix(color1,color2,0.5),color3,0.5);
}
