// CONTRAST=0.0 is "true" but doesn't look great in my opinion
#define CONTRAST 0.2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 webcam = texture(iChannel0, uv);
    float noise = texelFetch(iChannel1, ivec2(fragCoord) % textureSize(iChannel1, 0), 0).r;
    float webcamAvg = length(webcam.rgb) / sqrt(3.0);

    fragColor = vec4(webcamAvg > smoothstep(-CONTRAST, 1.0 + CONTRAST, noise));
}
