#define resolution iResolution
#define time iTime
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 r = resolution.xy, u = (2.* fragCoord - r)/r.y;
    u.y = abs(u.y);
    u *= mat2(cos(35.), -sin(45.),sin(45.),cos(45.)) + sin(time) * 0.005;
    u.x *= 10.;
    float pos = floor(u.x+ time);
    fragColor.r = mod(pos, 2.) * sin(pos* 0.5 + time);
    fragColor.g = mod(pos, 2.) * sin(pos* 0.5 + time + .6 );
    fragColor.b = mod(pos, 2.) * sin(pos* 0.5 + time + .8);
    fragColor *= (1.-abs(u.y *0.7));
}
