#define B (1.-fract(t*2.))
#define R(p,a,t) mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a)
#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    vec3 p,r=iResolution,c=vec3(0),
    d=normalize(vec3((C-.5*r.xy)/r.y,1));
    float i=0.,s,e,g=0.,t=iTime;
	for(;i++<99.;)
    {
        p=g*d;;
        p.z-=-t*2.;
        p=R(p,vec3(.577),clamp(sin(t/4.)*6.,-.5,.5)+.6);
        p=asin(sin(p));
        vec4 q=vec4(p,0);
        s=2.;
        for(int i=0;i++<4;){
            q=abs(q);
            q=q.x<q.y?q.zwxy:q.zwyx;
            s*=e=11./clamp(dot(q,q),.2,9.);
            q=q*e-vec4(6);
        }
        g+=e=abs(length(q.y)/s)+.002;
        c+=mix(vec3(1),H(log(s)*.3+.5),.5)*8e-5/e;  
    }
    c*=c*c;
    O=vec4(c,1);
}
