#define foldAmount 4

// snow function from https://www.shadertoy.com/view/XtBfzw
float Snow(vec2 uv,float scale)
{
	float w=smoothstep(1.,0.,-uv.y*(scale/10.));if(w<.1)return 0.;
	uv+=iTime/scale;uv.y+=iTime*2./scale;uv.x+=sin(uv.y+iTime*.5)/scale;
	uv*=scale;vec2 s=floor(uv),f=fract(uv),p;float k=3.,d;
	p=.5+.35*sin(11.*fract(sin((s+p+scale)*mat2(7,3,6,5))*5.))-f;d=length(p);k=min(d,k);
	k=smoothstep(0.,k,sin(f.x+f.y)*0.01);
    	return k*w;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // center the origin
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 1.5;

    vec2 mouse = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    float angle = (5./6.) * 3.1514;


    uv.x = abs(uv.x);
    uv.y += tan(angle) * .5;

    vec2 n = vec2(sin(angle), cos(angle));
    float d = dot(uv-vec2(.5, 0), n);
    uv -= n*max(0., d)*2.;

    n = vec2(sin((2./3.)* 3.1415), cos((2./3.)* 3.1415));
    float scale = 1.;

    // re center it
    uv.x += 0.5;
    for (int i = 0; i < foldAmount; i++)
    {
        uv *= 3.;
        scale *= 3.;
        uv.x -= 1.5;

        // mirror around the center
        uv.x = abs(uv.x);

        // expand the line
        uv.x -= .5;

        // mirror
        uv -= n*min(0., dot(uv, n))*2.;

    }

    // create line segment
    d = length(uv - vec2(clamp(uv.x, -1., 1.), 0));
    col += smoothstep(1./iResolution.y, .0, d/scale);
    uv /= scale;
    col.rg /= uv;

    float speed = 5.;

    col.rg *= vec2(tan(iTime * speed) / 2.0, cos(iTime * speed) / 4.0);

    float c=smoothstep(1.,0.3,clamp(uv.y*.3+.8,0.,.75));
	c+=Snow(uv,105.)*.3;
	c+=Snow(uv,104.)*.5;
	c+=Snow(uv,113.)*.8;
	c+=Snow(uv,12.);

    col += c;

    fragColor = vec4(col, 1.0);
}
