void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius = 100.0;
    vec2 position = vec2(500.0, 200.0);
    float antialiasing = 1.0;
    vec4 color = vec4(1.0, 0.0, 0.0, 1.0);
    vec4 background = vec4(0.0, 0.0, 0.0, 1.0);

    float distance = length(fragCoord - position);
    if (distance > radius + antialiasing/2.0) {
      fragColor = background;
    } else {
        float t = smoothstep(radius - antialiasing/2.0, radius + antialiasing/2.0, distance);
        fragColor = mix(color, background, t);
    }

}
