// This is a random idea that came into my mind.

// The man, the earth, and the graph of the Gamma function.
// What's beyond them?

// Modeling in Common.
// Rendering in Buffer A.

// Mouse-able.

void mainImage(out vec4 c, in vec2 g) {
    vec3 col = texelFetch(iChannel0, ivec2(g), 0).xyz;
    c = vec4(pow(col, vec3(.4545)), 1);
}

// Path tracing


// Random number generator
uint seed = 0u;
uint randu() { return seed = seed * 1664525u + 1013904223u; }
float randf() { return float(randu()) / 4294967296.; }



// Faked multi-scattering BRDF
vec3 sampleBrdf(
    vec3 wi, vec3 n,
    float alpha,  // roughness
    float f0,  // ratio of reflection along the normal
    vec3 albedo,
    inout vec3 m_col
    ) {

    vec3 u = normalize(cross(n, vec3(1.23, 2.34, -3.45)));
    vec3 v = cross(u, n);
    wi = vec3(dot(wi, u), dot(wi, v), dot(wi, n));
    vec3 wi0 = wi, wo, m;  // in, out and half vector

    // generate output ray
    for (int i = 0; i < 4; i++) {
        // generate a random GGX normal
        float su = 2.*PI*randf();
        float sv = randf();
        sv = atan(alpha*sqrt(sv/(1.-sv)));
        vec3 h = vec3(sin(sv)*vec2(cos(su),sin(su)), cos(sv));
        // reflect it about the GGX normal
        wo = -(wi-2.*dot(wi,h)*h);
        // if below surface, set it as the new in vector,
        // keep bouncing until it gives a good one
        if (wo.z < 0.) wi = -wo;
        else break;
    }
    wo.z = abs(wo.z);  // prevent below surface
    wi = wi0;
    m = normalize(wi+wo);

    // fresnel
    float F = mix(pow(1.-dot(wi, m), 5.), 1., f0);
    m_col *= F * albedo;
    return wo.x * u + wo.y * v + wo.z * n;
}



// Rendering

#define mat_none 0
#define mat_ground 1
#define mat_earth 2
#define mat_human 3

vec3 mainRender(vec3 ro, vec3 rd) {

    vec3 m_col = vec3(1), col;

    for (int iter = ZERO; iter < 8; iter++) {
        ro += 1e-2*rd;

        /* Intersection */

        vec3 n, min_n;
        float t, min_t = 1e3;
        int mat = mat_none;

        // plane and Î“
        t = -ro.z / rd.z;
        if (t > 0.) {
            min_t = t, min_n = vec3(0, 0, 1);
            mat = mat_ground;
        }
        t = raymarchGamma(ro, rd);
        if (t > 0. && t < min_t) {
            vec3 p = ro+rd*t;
            vec2 g = vec2(gamma(p.xy+vec2(.01,0))-gamma(p.xy-vec2(.01,0)),
                          gamma(p.xy+vec2(0,.01))-gamma(p.xy-vec2(0,.01)) )/.02;
            min_t = t, min_n = normalize(vec3(-g,1.));
            mat = mat_ground;
        }

        // earth
        t = min_t;
        if (intersectSphere(1., ro-EARTH_CENTER, rd, t, n)) {
            min_t = t, min_n = n;
            mat = mat_earth;
        }

        // human
        t = intersectHuman(ro-HUMAN_CENTER, rd);
        if (t > 0. && t < min_t) {
            min_t = t;
            min_n = normalize(gradHuman(ro+rd*t-HUMAN_CENTER, .01));
            mat = mat_human;
        }

        // update ray
        ro += rd * (t = min_t);
        n = faceforward(min_n, rd, min_n);

        /* Shading */

        col = vec3(1);
        if (mat == mat_none) {  // background
            col = texture(iChannel1, rd.xzy).xyz;
            return m_col * col;
        }
        if (mat == mat_ground) {  // plane
            float roughness = mix(.01, .05, smoothstep(0., 1., ro.z));
            rd = sampleBrdf(-rd, n, roughness, .5, vec3(.9,.9,.95), m_col);
        }
        if (mat == mat_earth) {  // earth
            vec3 ps = ro-EARTH_CENTER;
            col = mapEarth(vec3(-ps.y,ps.x,ps.z));
            col = smoothstep(0.,1.,sqrt(col));
            col = smoothstep(0.,1.,col);  // color adjustment
            rd = sampleBrdf(-rd, n, .5, 1., col, m_col);
        }
        if (mat == mat_human) {  // human
            rd = sampleBrdf(-rd, n, .5, .3, vec3(.3,.2,.1), m_col);
        }
    }
    return m_col;
}



// sample aparture
vec2 randomUnitDisk() {
    float a = 2.*PI*randf();
    return sqrt(randf())*vec2(cos(a), sin(a));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    ZERO = min(iFrame, 0);

    // random number seed
    vec3 p3 = fract(fragCoord.xyx*1.1031);
    p3 += dot(p3, p3.zxy + 31.32);
    float h = fract((p3.x + p3.y) * p3.z);
    seed = uint(1048576.*h)+uint(iFrame);

    // camera
    const float SCALE = 2.;  // larger = smaller (more view field)
    const float DIST = 8.; // larger = smaller
    const float VIEW_FIELD = .3;  // larger = smaller + more perspective
    const float APERTURE = .2;  // larger = more blurred

    float rx = iMouse.z==0.?.01*PI:1.8*(iMouse.y/iResolution.y)-0.2;
    float rz = iMouse.z==0.?-.69*PI:-4.*PI*iMouse.x/iResolution.x;
    vec3 w = vec3(cos(rx)*vec2(cos(rz),sin(rz)), sin(rx));
    vec3 u = vec3(-sin(rz),cos(rz),0);
    vec3 v = cross(w,u);
    vec3 ro = DIST*w + EARTH_CENTER+vec3(.5,0,0);
    vec2 uv = SCALE*(2.*(fragCoord.xy+vec2(randf(),randf())-.5)/iResolution.xy-1.);
    vec2 sc = iResolution.xy/length(iResolution.xy);
    vec2 offset = APERTURE*randomUnitDisk();
    vec3 rd = mat3(u,v,-w)*normalize(vec3(VIEW_FIELD*uv*sc+offset/DIST, 1.));
    ro -= offset.x*u+offset.y*v;

    // calculate/update pixel color
    vec3 col = mainRender(ro, rd);
    col = dot(col,vec3(1))>0. ? col : vec3(0);  // kill NAN
    vec4 rgbn = texelFetch(iChannel0, ivec2(fragCoord), 0);
    if (iMouse.z>0.) rgbn.w = 0.;
    fragColor = vec4((rgbn.xyz*rgbn.w + col)/(rgbn.w+1.), rgbn.w+1.);
}
