vec3 hueShift(vec3 c,float shift){
    float Max = max(max(c.x,c.y),c.z);
    float Min = min(min(c.x,c.y),c.z);
    float chr = Max-Min;
    float hue = 0.;
    if(Max == c.x){
        hue = (c.y-c.z)/chr;
    }else if(Max == c.y){
        hue = 2.+(c.z-c.x)/chr;
    }else{
        hue = 4.+(c.x-c.y)/chr;}
    float sat = chr/Max;
    float val = Max;
    hue += shift;
    hue = mod(hue,6.);

    float x = chr*(1.-abs(mod(hue,2.)-1.));
    vec3 rgb = vec3(0.);
    if(hue>=0.,hue<=1.){
        rgb = vec3(chr,x,0.);}
    else if(hue>1.,hue<=2.){
        rgb = vec3(x,chr,0.);}
    else if(hue>2.,hue<=3.){
        rgb = vec3(0.,chr,x);}
    else if(hue>3.,hue<=4.){
        rgb = vec3(0.,x,chr);}
    else if(hue>4.,hue<=5.){
        rgb = vec3(x,0.,chr);}
    else{
        rgb = vec3(chr,0.,x);};
    return rgb+val-chr;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 c = texture(iChannel0,uv).xyz;

    fragColor = vec4(hueShift(c,iTime),1.0);
}
