const float PI = 3.14;

const float T = PI;
const float W0 = 3./4.;
const float W1 = 1./4.;

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    float t = mod(iTime, T);
    vec2 U0 = rotZ(U, W0 * t);
    vec2 U1 = rotZ(U, W1 * t);
    vec4 l0 = vec4(min(.01/abs(U0.y), 1.));
    vec4 l1 = vec4(min(.01/abs(U1.y), 1.));
    O = (0.5 * l0 + l1) * vec4(sin(iTime) * round(atan((U.x - cos(iTime * .1) * U.y) * 2.) * 5.)/5. + cos(iTime) * round(atan((U.y + sin(iTime * .1) * U.x) * 2.) * 5.)/5. + 1.);
}
