vec3 to_rgb(int r, int g, int b) {
return vec3(float(r) / 256.0, float(g) / 256.0, float(b) / 256.0);
}

vec3 to_rgb(float r, float g, float b) {
return vec3(r / 256.0, g / 256.0, b / 256.0);
}

float rand(vec2 co){
return fract(sin(dot(co, vec2(1.9898, 7.233))) * 0.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col;

    if (abs(uv.x - uv.y) <= 0.1 ||
        abs(1.0 - uv.x - uv.y) <= 0.1 ||
        abs(uv.x - 0.5) <= 0.08 ||
        abs(uv.y - 0.5) <= 0.15) 
    {
        col = vec3(1.0, 1.0, 1.0);
    } else {
        col = to_rgb(0.0, 0.0, 102.0);
    }

    if (abs(uv.x - 0.5) <= 0.05 ||
        abs(uv.y - 0.5) <= 0.1 ||
        (abs(uv.x - uv.y + 0.02) <= 0.04 && uv.y >= 0.65) ||
        (abs(uv.x - uv.y - 0.02) <= 0.04 && uv.y <= 0.35) ||
        (abs(1.0 - uv.x - uv.y - 0.02) <= 0.04 && uv.y >= 0.65) ||
        (abs(1.0 - uv.x - uv.y + 0.02) <= 0.04 && uv.y <= 0.35)) 
    {
        col = mix(col, to_rgb(204, 0, 0), 1.0);
    }

    float r = 0.99 - sqrt((0.5 - uv.x)*(0.5 - uv.x) + (0.5 - uv.y)*(0.5 - uv.y));
    float rd = rand(uv.xy);
    col = col * vec3(r,r,r) * rd;

    // Output to screen
    fragColor = vec4(col,1.0);
}
