float lineSegment(vec2 p, vec2 a, vec2 b){    
    vec2 ba = b-a;
    vec2 pa = p-a;
    float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa-h*ba);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    float d = lineSegment(p, vec2(-0.5,0), vec2(0,0.5));
    d = min(d, lineSegment(p, vec2(0,0), vec2(0.5,-0.5)));
    d = min(d, lineSegment(p, vec2(0.5,-0.5), vec2(0.5,0)));

    vec3 col = vec3(0.4,0.2,0.8);

    float height = smoothstep(-0.1, 0.1,sin(d * 20.0 * 3.14 + -iTime*2.0));

    col *= 0.4 + height * 0.6;

    col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015, d ));

    // Output to screen
    fragColor = vec4(col, 1.0);
}
