float closestStep(float stIN, float valIN) {
    float m = 255.0;

    float st = stIN * m;
    float val = valIN * m;

    float o = round(st * val / m) * m / st;

    return o / m;
}

const float depth = 3.0 / 255.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 oldtemp = texture(iChannel0, uv);

    float tempr = closestStep(depth, oldtemp.r);
    float tempg = closestStep(depth, oldtemp.g);
    float tempb = closestStep(depth, oldtemp.b);

    vec4 temp = vec4(tempr,tempg,tempb, 1.0);




    fragColor = temp;
}
