float circle(vec2 a, vec2 point, float r, float blur)
{
    float b=length(a-point);
    float c=smoothstep(r,r-blur,b);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 a = fragCoord.xy/iResolution.xy;
    a-=.5;
    a.x*=iResolution.x / iResolution.y;
    vec3 color=vec3(9.);

    float c=circle(a, vec2(.0, .0), .3, .02); // body

    c+=circle(a, vec2(-.2, .2), .057, .01); //  ухo лев основа
    c-=circle(a, vec2(-.25, .195), .037, .01); // ухо лев доп        
    c+=circle(a, vec2(.2, .2), .057, .01); //  ухо прав основа
    c-=circle(a, vec2(.25, .195), .037, .01); // ухо прав доп 

    c-=circle(a, vec2(.0, -.03), .06, .01); // нос
    c+=circle(a, vec2(-.018, -.04), .015, .01); //ноздря 1
    c+=circle(a, vec2(.018, -.04), .015, .01); //ноздря 2   

    c-=circle(a, vec2(.0, -.138), .05, .01); // рот основа
    c+=circle(a, vec2(.0, -.128), .045, .01); // рот тень 

    c+=circle(a, vec2(-.3, .0), .06, .01); // рука левая 1
    c+=circle(a, vec2(-.33, -.04), .05, .01); // рука левая 2
    c+=circle(a, vec2(-.34, -.08), .05, .01); // рука левая 3        
    c+=circle(a, vec2(.3, .0), .06, .01); // рука правая 1
    c+=circle(a, vec2(.33, -.04), .05, .01); // рука правая 2
    c+=circle(a, vec2(.34, -.08), .05, .01); // рука правая 3

    c+=circle(a, vec2(.07, -.30), .06, .01); // нога прав основа
    c+=circle(a, vec2(.08, -.35), .045, .01); // нога прав доп        
    c+=circle(a, vec2(-.07, -.30), .06, .01); // нога лев основа
    c+=circle(a, vec2(-.08, -.35), .045, .01); // нова лев доп

    c-=circle(a, vec2(-.077, .08), .08, .01); // глаз левый основа
    c+=circle(a, vec2(-.045, .055), .025, .01); // зрачок левый
    c-=circle(a, vec2(-.035, .063), .014, .01); // точка в зрачке левый        
    c-=circle(a, vec2(.077, .08), .08, .01); // глаз правый основа
    c+=circle(a, vec2(.045, .055), .025, .01); // зрачок правый
    c-=circle(a, vec2(.055, .063), .014, .01); // точка в зрачке правый



    color=vec3(2., 0., 2.)* c;
    fragColor = vec4(color, 3);
    }
