void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float dist = 20.0;
    float quality = 1.0;
    //lower is better
    vec2 uv = fragCoord.xy/iResolution.xy;
    vec4 texColor = texture(iChannel0,uv);
    for (float i = 1.0; i < dist; i+=10.0) {
        float x1 = (i*quality)/iResolution.x;
        float y1 = (i*quality)/iResolution.y;
        texColor = (texColor + texture(iChannel0,vec2(uv.x-x1,uv.y))) / 2.0;
        texColor = (texColor + texture(iChannel0,vec2(uv.x+x1,uv.y))) / 2.0;
        texColor = (texColor + texture(iChannel0,vec2(uv.x,uv.y-y1))) / 2.0;
        texColor = (texColor + texture(iChannel0,vec2(uv.x,uv.y+y1))) / 2.0;
    }
    fragColor = texColor;
}
