float sum(vec2 v) { return v.x + v.y; }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.95);
    float t = iTime*2.;

    // Pixel Normalizations //
    vec2 uv01 = fragCoord.xy/iResolution.xy,
         uv = (uv01 - 0.5) * vec2(iResolution.x / iResolution.y, 1) * 3.6;

    vec2 cpos = uv*24.5 + vec2(0, -t*2.);
    if (sum(cos(cpos)) > 1.6) col = vec3(0.9); // Circles //

    // Thor //
    float r = sin(t/4.)*0.2-0.7, R = 1.;

    for (float z=2.; z >=-2.; z-=0.03) {
        vec3 v = vec3(uv.x, uv.y, z);
        v.zy *= mat2( cos(t), -sin(t), sin(t), cos(t) );
        v.yx *= mat2( cos(t/1.2), -sin(t/1.2), sin(t/1.2), cos(t/1.2) );

        if (sqrt( pow( sqrt(v.x*v.x + v.z*v.z)-R, 2.) + pow(v.y, 2.) ) - r < 1.) {
            col = vec3(uv01.xy, 1.) * vec3((z*.5+.5)/1.5+0.65);
            break;
        }
    } 

    col *= (1. - length((uv01-0.5)/2.))*1.1; // Vignette //

    fragColor = vec4(col,1.);
}
