// simplified from https://shadertoy.com/view/stXyDM

void mainImage( out vec4 O, vec2 U )
{

    float Q =  -1.;             // on Windows, give black.
 // const float Q = -1.;        // grey on Windows and OpenGL.

    float t =  pow(Q , .333) ;  // should be NaN

    O = vec4(0.);
    if (isnan(t)) O = vec4(.5); // grey if Nan or Inf or < 0 or = 0 pr > 0 
    if (t <= 0.)  O = vec4(.5); // -> how can it be black ?
    if (t >= 0.)  O = vec4(.5);
    if (isinf(t)) O = vec4(.5);

}
