// возвращает остаток от деления
int imod(int n, int d)
{
  return n - d * (n / d) * int (n > d);
}


// генератор значения от 0.0 до 1.0 для заданного диапазона
// времени заданного в секундах
float gen_ftime(float range)
{
    int ms = int(iTime * 1000.0);
    ms = imod(ms, int(range));
    return float(ms) / range;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    fragColor = vec4(0.2, 0.2, 0.2, 1.0);
    float fms = gen_ftime(5000.0); // 0 .. 1.0
    fragColor *= float(abs(uv.y - fms) < 0.01);      
}
