{
    O = vec4(0,I=1.2-I/iResolution.x/.4,1.-dot(I,I++));
    O = I.y/(.7+abs(O.wwww)*1e2)+.8*max(O.z-(O.w>0.?sqrt(O.w)-.3:.2),0.);
    O.xy *= I*.3; O*=O;
}
