//CLICK MOUSE AND MOVE


#define circle length(o-M.xy)

void mainImage(out vec4 c,in vec2 o){

  c=vec4(0);
  vec3 verse,universe,miniverse;
  vec2 M,R=iResolution.xy;
  float r=o.x*o.x+o.y*o.y,t=iTime*radians(180.0);
  M=((iMouse.x+iMouse.y>0.0)?iMouse.xy:R/2.0+1.0);

  verse=vec3(1,1,1);
  universe=sin(r*(M.x/length(R)+M.y/length(R))+t)*vec3(0,1,1);
  miniverse=cos(r*(M.x/length(R)+M.y/length(R))+t)*vec3(1,0,0);

  if(int(circle/2.0)==int(256.0/4.0))c=vec4(verse,1);
  else if(circle>=256.0/2.0)c=vec4(universe,1.);
  else c=vec4(miniverse,1);
}
