void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

float lineseg(vec2 uv, vec2 pa, vec2 pb) {
    vec2 a = uv-pa; 
    vec2 b = pb-pa;    
    vec2 proj = clamp((dot(a,b)/dot(b,b)),0.0,1.0)*b; 
    vec2 rejc = a-proj;  
    return 1.9-dot(rejc, rejc)*1e4;
}

vec3 inferno(float x) {
    vec3 c0 = vec3(0.0002189403691192265, 0.001651004631001012, -0.01948089843709184);
    vec3 c1 = vec3(0.1065134194856116, 0.5639564367884091, 3.932712388889277);
    vec3 c2 = vec3(11.60249308247187, -3.972853965665698, -15.9423941062914);
    vec3 c3 = vec3(-41.70399613139459, 17.43639888205313, 44.35414519872813);
    vec3 c4 = vec3(77.162935699427, -33.40235894210092, -81.80730925738993);
    vec3 c5 = vec3(-71.31942824499214, 32.62606426397723, 73.20951985803202);
    vec3 c6 = vec3(25.13112622477341, -12.24266895238567, -23.07032500287172);
    return c0+x*(c1+x*(c2+x*(c3+x*(c4+x*(c5+x*c6)))));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy/iResolution.xy;

    if(int(fragCoord.y) == int(iResolution.y) - 46){
        float fft = texture(iChannel0, vec2(uv.x, 0.25)).x;

        //inferno colormap
        fragColor = vec4(inferno(fft), 1.0);     
    }else if(int(fragCoord.y) < int(iResolution.y) - 46){
        fragColor = texture(iChannel1, (fragCoord + vec2(0.0, 1.0)) / iResolution.xy);
    }else{
        //do loudness visualization
        uv.y = (fragCoord.y - (iResolution.y - 44.0))/(iResolution.y - (iResolution.y - 44.0));
        float c = texture(iChannel0, vec2(uv.x, 0.25)).x;
        float p = texture(iChannel0, vec2(uv.x - 1.0/iResolution.x, 0.25)).x;      
        fragColor = vec4(vec3(max(lineseg(uv, vec2(uv.x, p), vec2(uv.x, c)), 0.1)), 1.0);
    }
}
