#define S(a,b,t) smoothstep(a,b,t)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(texture(iChannel0, uv));
    fragColor = vec4(col,1.0);
}

vec3 kernel(ivec2 fragCoord){
    vec3 num = vec3(0.);
    vec3 preNum = texelFetch(iChannel1, fragCoord, 0).rgb;
    mat3 matrixR;
    mat3 matrixG;
    mat3 matrixB;
    for(int i = -1; i < 2 ; i++ ){
        for(int j = -1; j < 2 ; j++ ){
            if(i != 0 || j != 0)
                num += texelFetch(iChannel1, fragCoord + ivec2(i,j), 0).rgb;
            matrixR[i+1][j+1]= texelFetch(iChannel1, fragCoord + ivec2(i,j), 0).r;
            matrixG[i+1][j+1]= texelFetch(iChannel1, fragCoord + ivec2(i,j), 0).g;
            matrixB[i+1][j+1]= texelFetch(iChannel1, fragCoord + ivec2(i,j), 0).b;
        }
    }
    mat3 matrix2 = mat3(
        1,0,0,
        0,1,0,
        0,0,1
    );
    //num = matrix2*matrixR;
    if((num/8.0 - preNum).r > 0.){
        preNum = preNum + (num/8.0 - preNum)*2.1;
    }else
        preNum = preNum + (num/8.0 - preNum)*.1;

    return preNum;
}

vec3 getColor(ivec2 fragCoord){
    vec3 num = kernel(fragCoord);
    return num;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(texture(iChannel0, uv));

    /**/
    if(iFrame < 10){
        col = vec3(texture(iChannel0, uv));
    }else{
        col = getColor(ivec2(fragCoord));
    }
    /**/
    fragColor = vec4(col,1);
}
