float zagRight(in vec2 _st) {
    return step(0.05, abs(_st.x) - abs(_st.y));
}

float zagLeft(in vec2 _st) {
    return step(0.1, (_st.x) - abs(_st.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/100.*(iResolution.y/iResolution.x);


    vec3 col = vec3(1.);

    vec2 pos = floor(st);
        st.x += iTime * 0.5 + pos.y;
       st = 4.*(fract(st )-0.5);

    if (mod(pos.x+pos.y,2.)>0.) {
        col *= zagLeft(st);
    } else {
        col *= zagRight(st);
    }



    col.r *= mod(pos.x, 3.4)/2. * (1. + 0.3* sin(iTime));
    col.g *= mod(pos.y, 3.1)/1.9 * (1. + 0.3* sin(iTime));
    col.b *= mod(pos.x + pos.y + 1., 3.7)/1.9* (1. + 0.5* sin(iTime+0.6));




    // Output to screen
    fragColor = vec4(col,1.0);
}
