mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


float fract2(float x){
    return abs((fract(x)-.5)*2.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Initialisation
    vec2 st = fragCoord/iResolution.y;
    vec3 color = vec3(1.0);
    float t = iTime*.5;
    //remap
    st = st*2.-1.;

    st = rotate2d(t*.9)*st;
    float r = length(st)*.9,th = atan(st.y,st.x);
    for(int i=0;i<3;i++){
        color[i] *= smoothstep(.4,.6,fract2(r*(abs(sin(th*2.5+float(i)*.07))+1.)*.85+t*2.));
    }

    fragColor = vec4(color,1.0);
}
