void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 p=(2.5*fragCoord.xy-iResolution.xy)/max(iResolution.x,iResolution.y);

    const float Pi = 3.14159;
    float beat = 0.;
	int tx = int(uv.y);
    float react = texelFetch( iChannel0, ivec2(tx,0), 0 ).x*0.05;
    float ct = iChannelTime[0];
    beat = atan (ct)*Pi*2.+iTime*0.55+0.55,15.0;
	for(int i=1;i<40;i++)
	{
		vec2 np=p;
		np.x+=.55/float(i)*sin(float(i)*p.y+iTime+beat*0.25+0.25*sin(ct)*0.3/40.0+0.5)+10.0;		
		np.y+=.55/float(i)*sin(float(i)*p.x+iTime+beat*0.25+0.25*sin(ct)*0.3/50.0+0.5)+15.0;
		p=np;
	}

	vec3 col=vec3(.91*sin(9.1*p.x)+0.5,sin(1.1*p.y),sin(p.x+p.y))+react;
	fragColor=vec4(col+react*15.+0.5*sin((uv.x * uv.y)*(uv.y / uv.x)), 1.0);

}
